import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Jumbotron from "react-bootstrap/Jumbotron";
import "./BestBooks.css";
import axios from "axios";
class BestBooks extends React.Component {
  constructor(proos) {
    super(props);
    this.state = {
      arr: [],
    };
  }
  componentDidMount = () => {
    const { isAuthenticated, user } = useAuth0();
    url = `http://localhost:3010/get?email=${user.email}`;
    axios
      .get(url)
      .then((result) => {
        this.setState({
          arr: result.data,
        });
        console.log(result.data)
      })
      .catch((err) => {
        console.log("err in getting");
      });
  };

  postdata = () => {

    obj={
      instructions:event.target.instructions.value,
      name:event.target.name.value
    }

    url = `http://localhost:3010/postdata`,obj;
     axios
    .post(url)
    .then((result) => {
      this.setState({
        arr: result.data,
      });
    })
    .catch((err) => {
      console.log("err in getting");
    });};

  update = (event) => {
    preventDefault()
    obj={
      instructions:event.target.instructions.value,
      name:event.target.name.value
    }

    url = `http://localhost:3010/updatedata/id`,obj;
     axios
    .put(url)
    .then((result) => {
      this.setState({
        arr: result.data,
      });
    })
    .catch((err) => {
      console.log("err in getting");
    });};









  render() {
    return <></>;
  }
}

export default BestBooks;
